import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Card, CardBody, CardHeader, Col, Row, Table, Pagination, PaginationItem, PaginationLink } from 'reactstrap';
import wretch from 'wretch'
import qs from 'query-string'
import Moment from 'moment';
import axios from 'axios'
import { BASE_PATH, SIGNAL} from '../../api/paths/index'
import { loadSignals } from '../../api/functions/SignalsFunctions'

function SignalRow(props) {
  const signal = props.signal
  const signalLink = `/signal/${signal._id}`


  return (
    <tr key={signal._id}>
      <td><Link to={signalLink}>{signal.name}</Link></td>
      <td>{Moment(signal.date).format('DD/MM/YY à HH:mm')}</td>
      <td>{Moment(signal.created_date).format('DD/MM/YY à HH:mm')}</td>
      <td>{Moment(signal.last_update).format('DD/MM/YY à HH:mm')}</td>
      <td>{signal.status}</td>
      <td>MODIFIER / SUPPRIMER </td>
    </tr>
  )
}

class Signals extends Component {
  _isMounted = false;

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      page: 1,
      pageTotal: 1,
      offset: 10,
      total: 0,
      isLoading: false
    };
  }

  componentDidMount() {
    this._isMounted = true;
    const parsed = qs.parse(this.props.location.search, {parseNumbers: true});
    const page = parsed && parsed.page && typeof parsed.page === "number" ? parsed.page : 1;
    //loadSignals(page);
    this.setState({
      page
    });
    loadSignals(page,axios.defaults.headers.common.Bearer).then(response => {
      if (this._isMounted)
           this.setState({
             isLoading: true
           });
      if (response) {
        if (this._isMounted) {
          this.setState({
             isLoading: false,
             items: response
           })
           }
      }
    })
  }

  componentWillUnmount() {
    this._isMounted = false;
  }

  async componentDidUpdate(prevProps, prevState, snapshot) {
    if (prevProps.location.search !== this.props.location.search) {
      const parsed = qs.parse(this.props.location.search, {parseNumbers: true});
      const page = parsed && parsed.page && typeof parsed.page === "number" ? parsed.page : 1;
      if (this._isMounted)
        this.setState({
          page
        });
      console.log('page', page)
      this.loadSignals(page)
    }
  }




  // loadSignals = (page = 1) => {
  //   if (this._isMounted)
  //     this.setState({
  //       isLoading: true
  //     });
  //     axios
  //      .get(BASE_PATH + SIGNAL, {
  //      headers: { Authorization: `Bearer ${axios.defaults.headers.common.Bearer}` }
  //     })
  //      .then(response => {
  //        console.log(response);
  //       if (this._isMounted) {
  //         this.setState({
  //            isLoading: false,
  //            items: response
  //          })
  //          }
  //         })
  //       .catch(err => {
  //            console.log(err)
  //       })
  // }


  renderPagination = () => {
    const { page, totalPage } = this.state

    let pagination = []
    for (let i = 1; i <= totalPage; i++) {
      pagination.push(
        <PaginationItem key={i} active={page === i}>
          <PaginationLink href={`#/signal?page=${i}`}>
            {i}
          </PaginationLink>
        </PaginationItem>
      )
    }

    return <Pagination>{pagination}</Pagination>
  }

  render() {
    let signalsList = this.state;
    let listItems
    if (signalsList.items.data) {
          signalsList = signalsList.items.data
          listItems = signalsList.map((signal, index) => <SignalRow key={index} signal={signal}/>)
      }
      console.log(signalsList);
    return (
      <div className="animated fadeIn">
        <Row>
          <Col xl={12}>
            <Card>
              <CardHeader>
                <i className="fa fa-align-justify"></i> Signalements <small className="text-muted">{signalsList.length}</small>
              </CardHeader>
              <CardBody>
                <Table responsive hover>
                  <thead>
                  <tr>
                    <th scope="col">Nom</th>
                    <th scope="col">Date</th>
                    <th scope="col">Date de création</th>
                    <th scope="col">Dernière modification</th>
                    <th scope="col">Status</th>
                    <th scope="col">Action</th>
                  </tr>
                  </thead>
                  <tbody>
                  {listItems}
                  </tbody>
                </Table>
              </CardBody>
            </Card>
          </Col>
          <Col xl={12}>
            {this.renderPagination()}
          </Col>
        </Row>
      </div>
    )
  }
}

export default Signals;
