import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Icons, Button, Badge, Card, CardBody, CardHeader, Col, Row, Table, Pagination, PaginationItem, PaginationLink } from 'reactstrap';
import wretch from 'wretch'
import qs from 'query-string'
import Moment from 'moment';
import { loadSignals } from '../../api/functions/SignalsFunctions'

function SignalRow(props) {
  let signal = props.signal
  let signalLink = `/signal/${signal._id}`
  const getBadge = (status)=>{
    switch (status) {
      case 'Terminé': return 'success'
      case 'En Cours': return 'warning'
      case 'Ouvert': return 'info'
      case 'En Attente': return 'danger'
      default: return 'primary'
    }
  }

  return (
    <tr key={signal._id}>
      <td><Link to={signalLink}>{signal.name}</Link></td>
      <td>{Moment(signal.date).format('DD/MM/YY à HH:mm')}</td>
      <td>{Moment(signal.created_date).format('DD/MM/YY à HH:mm')}</td>
      <td>{Moment(signal.last_update).format('DD/MM/YY à HH:mm')}</td>
      <td> <Badge color={getBadge(signal.status)}> {signal.status} </Badge> </td>
      <td><strong>{signal.category}</strong></td>
      <td>MODIFIER</td>
    </tr>
  )
}

class Signals extends Component {
  _isMounted = false;

  constructor(props) {
    super(props);
    this.state = {
      items: [],
      page: 1,
      pageTotal: 1,
      offset: 10,
      total: 0,
      isLoading: false
    };
  }

  componentDidMount() {
    this._isMounted = true;
    const parsed = qs.parse(this.props.location.search, {parseNumbers: true});
    const page = parsed && parsed.page && typeof parsed.page === "number" ? parsed.page : 1;
    this.setState({
      page
    });
      loadSignals(page).then(response => {
        if (this._isMounted)
           this.setState({
             isLoading: true
           });
      if (response) {
        if (this._isMounted) {
          this.setState({
             isLoading: false,
             items: response
           })
           }
      }
    })
  }

  componentWillUnmount() {
    this._isMounted = false;
  }

  async componentDidUpdate(prevProps, prevState, snapshot) {
    if (prevProps.location.search !== this.props.location.search) {
      const parsed = qs.parse(this.props.location.search, {parseNumbers: true});
      const page = parsed && parsed.page && typeof parsed.page === "number" ? parsed.page : 1;
      if (this._isMounted)
        this.setState({
          page
        });
      console.log('page', page)
      loadSignals(page)
    }
  }

  renderPagination = () => {
    const { page, totalPage } = this.state

    let pagination = []
    for (let i = 1; i <= totalPage; i++) {
      pagination.push(
        <PaginationItem key={i} active={page === i}>
          <PaginationLink href={`#/signal?page=${i}`}>
            {i}
          </PaginationLink>
        </PaginationItem>
      )
    }

    return <Pagination>{pagination}</Pagination>
  }

  createSignals = (signalsList) => {
    let  listItems = signalsList.map((signal, index) => <SignalRow key={index} signal={signal}/>)
    return listItems
  }

   SortByName = (listItems) => {
    console.log("SortByName Function Trigged");
    if (listItems !== undefined)
      listItems.sort((a, b) => {
     if (a.props.signal.name.toUpperCase() < b.props.signal.name.toUpperCase()) {
       return -1;
     }
     if (a.props.signal.name.toUpperCase() > b.props.signal.name.toUpperCase()) {
       return 1;
     }
     return 0;
   });
   const newState = this.state;
   listItems.forEach(function (item, index) {
     newState.items.data[index] = item.props.signal
    });
   this.setState({ items: newState.items });
   this.createSignals(listItems)
   this.ProductTable(listItems)
  }

   SortByStatus = (listItems) => {
    console.log("SortByStatus Function Trigged");
    if (listItems !== undefined)
      listItems.sort((a, b) => {
     if (a.props.signal.status.toUpperCase() < b.props.signal.status.toUpperCase()) {
       return -1;
     }
     if (a.props.signal.status.toUpperCase() > b.props.signal.status.toUpperCase()) {
       return 1;
     }
     return 0;
   });
   const newState = this.state;
   listItems.forEach(function (item, index) {
     newState.items.data[index] = item.props.signal
    });
   this.setState({ items: newState.items });
   this.createSignals(listItems)
   this.ProductTable(listItems)
  }

   SortByCategory = (listItems) => {
    console.log("SortByCategory Function Trigged");
    if (listItems !== undefined)
      listItems.sort((a, b) => {
     if (a.props.signal.category.toUpperCase() < b.props.signal.category.toUpperCase()) {
       return -1;
     }
     if (a.props.signal.category.toUpperCase() > b.props.signal.category.toUpperCase()) {
       return 1;
     }
     return 0;
   });
   const newState = this.state;
   listItems.forEach(function (item, index) {
     newState.items.data[index] = item.props.signal
    });
   this.setState({ items: newState.items });
   this.createSignals(listItems)
   this.ProductTable(listItems)
  }

   SortByDate = (listItems) => {
    console.log("SortByDate Function Trigged");
    if (listItems !== undefined)
      listItems.sort((a, b) => {
          return new Date(b.props.signal.date) - new Date(a.props.signal.date)
        });
        const newState = this.state;
        listItems.forEach(function (item, index) {
          newState.items.data[index] = item.props.signal
         });
        this.setState({ items: newState.items });
        this.createSignals(listItems)
        this.ProductTable(listItems)
      }

ProductTable = (listItems) => {

  return(
  <Table responsive hover border striped sorter>
    <thead>
    <tr>
      <th scope="col"><Button type="button" onClick={() => this.SortByName(listItems)}>Nom</Button></th>
      <th scope="col"><Button type="button" onClick={() => this.SortByDate(listItems)}>Date</Button></th>
      <th scope="col"><Button type="button" onClick={() => this.SortByDate(listItems)}>Date de création</Button></th>
      <th scope="col"><Button type="button" onClick={() => this.SortByDate(listItems)}>Dernière modification</Button></th>
      <th scope="col"><Button type="button" onClick={() => this.SortByStatus(listItems)}>Status</Button></th>
      <th scope="col"><Button type="button" onClick={() => this.SortByCategory(listItems)}>Categorie</Button></th>
      <th scope="col"><i class="fal fa-sort"></i>Action</th>
    </tr>
    </thead>
    <tbody>
    {listItems}
    </tbody>
  </Table>
  )
}

  render() {
    let signalsList = this.state;
    let listItems
    if (signalsList.items.data) {
          signalsList = signalsList.items.data
          listItems = this.createSignals(signalsList)
      }
    return (
      <div className="animated fadeIn">
        <Row>
          <Col xl={12}>
            <Card>
              <CardHeader>
                <i className="fa fa-align-justify"></i> Signalements <small className="text-muted">{signalsList.length}</small>
              </CardHeader>
              <CardBody>
                {this.ProductTable(listItems)}
              </CardBody>
            </Card>
          </Col>
          <Col xl={12}>
            {this.renderPagination()}
          </Col>
        </Row>
      </div>
    )
  }
}

export default Signals;
