     .get(BASE_PATH + SIGNALS, {
     headers: { Authorization: `Bearer ` + localStorage.getItem('Token') }
    })
     .then(response => {
       console.log(response);
       return response
        })
      .catch(err => {
           console.log(err)
      })
}

