        .patch(BASE_PATH + SIGNAL(signal_id), {
          headers: { Authorization: `Bearer ` + localStorage.getItem('Token') }
        }, {
          _id: newSignal._id,
          name: newSignal.name,
          category: newSignal.category,
          description: newSignal.description,
          author: newSignal.author,
          city: newSignal.city,
          created_date: newSignal.created_date,
          date: newSignal.date,
          last_update: newSignal.last_update,
          localization: {int: newSignal.localization.int, long: newSignal.localization.long},
