import React, { Component } from 'react';
import {Badge, Card, CardBody, CardHeader, Col, Row, Table} from 'reactstrap';
import wretch from 'wretch';
import Moment from 'moment';
import mapboxgl from 'mapbox-gl'
import { relative } from 'path';


class SignalsMap extends Component {
  _isMounted = false;

  constructor(props) {
    super(props);
    this.state = {
      items: {},
      isLoading: false
    };
  }

  async componentDidMount() {
    this._isMounted = true;
    const res = await this.loadSignal();

    mapboxgl.accessToken = 'pk.eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI1ZGI4MTIzOWRmN2U2ZDRiNjgwZjQyZTEiLCJpYXQiOjE2MDA3Nzk3ODZ9.pmNOT1kl1a-ylGCOudg8JVabUOAuW3gRjuh2zSCmruQ';

    var map = new mapboxgl.Map({
      container: this.Mapcontainer,
      style: 'mapbox://styles/mapbox/streets-v9',
      center: [2.4159, 48.7651],
      zoom: 13
    });

    map.on("load", function () {
          map.loadImage("https://i.imgur.com/MK4NUzI.png", async function(error, image) {
          if (error) throw error;
          await res.forEach(
            sig => {
            map.addImage(sig._id, image);
            map.addLayer({
              id: sig._id,
              type: "symbol",
              source: {
                type: "geojson",
                data: {
                  type: 'FeatureCollection',
                  features: [
                    {
                      type: 'Feature',
                      properties: {
                        "description":"<strong>"+ sig.name +"</strong><p>"+
                        sig.description + "</p> <p>Date: "+ Moment(sig.created_date).format('DD/MM/YY à HH:mm') +
                        "</p> <p><a href='"+ `/signal/${sig._id}/` +"'><em>Voir la fiche du signalement</em></a> </p>"
                      },
                      geometry: {
                      type: "Point",
                      coordinates: [sig.localization.long, sig.localization.lat]
                      }
                    }
                  ]
                }
              },
              layout: {
                "icon-image": sig._id,
                "icon-allow-overlap": true
              }
            });
            map.on('click', sig._id, function (e) {
              var coordinates = e.features[0].geometry.coordinates.slice();
              var description = e.features[0].properties.description;
              while (Math.abs(e.lngLat.lng - coordinates[0]) > 180) {
              coordinates[0] += e.lngLat.lng > coordinates[0] ? 360 : -360;
              }
              new mapboxgl.Popup()
              .setLngLat(coordinates)
              .setHTML(description)
              .addTo(map);
              });
            }
          );
        })
      });
  }

  componentWillUnmount() {
    this._isMounted = false;
  }

  async componentDidUpdate(prevProps, prevState, snapshot) {
    if (prevProps.match.params.id !== this.props.match.params.id)
      await this.loadSignal()
  }

  loadSignal = async () => {
    return new Promise((resolve, reject) => {
      if (this._isMounted)
        this.setState({
          isLoading: true
        });
      wretch()
        .auth(`Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI1ZGI4MTIzOWRmN2U2ZDRiNjgwZjQyZTEiLCJpYXQiOjE2MDA3Nzk3ODZ9.pmNOT1kl1a-ylGCOudg8JVabUOAuW3gRjuh2zSCmruQ`)
        .url(`http://localhost:8080/signal`)
        .get()
        .json(res => {
          if (this._isMounted)
           this.setState({
            isLoading: false,
            items: res,
          });
          resolve(res)
        })
        .catch(err => {
          console.log('err', err);
          reject()
        })
    })
  }

  render() {
    return (
      <div className="animated fadeIn">
        <Row>
          <Col lg={12}>
            <Card>
              <CardHeader className="card card-accent-warning mb-3" >
                <strong><i className="icon-info pr-1"></i>Localisation</strong>
              </CardHeader>
              <CardBody>
              <div className='FullMap' ref={(x) => { this.Mapcontainer = x }}></div>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
    )
  }
}

export default SignalsMap;
