import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Label, Button, Card, CardBody, CardFooter, Col, Container, Form, Input, InputGroup, InputGroupAddon, InputGroupText, Row } from 'reactstrap';
import { login } from '../UserFunctions'

class Login extends Component {

  constructor() {
     super()
     this.state = {
       email: '',
       password: '',
       errors: {}
     }

     this.onChange = this.onChange.bind(this)
     this.onSubmit = this.onSubmit.bind(this)
   }

   onChange(e) {
     this.setState({ [e.target.name]: e.target.value })
   }
   onSubmit(e) {
     e.preventDefault()

     const user = {
       email: this.state.email,
       password: this.state.password
     }


   login(user).then(res => {
     if (res) {
       this.props.history.push(`/Signals`) //may be add on app.js
     }
   })
 }

render() {
  return (
    <div className="app flex-row align-items-center">
      <Container>
        <Row className="justify-content-center">
          <Col md="9" lg="7" xl="6">
            <Card className="mx-4">
              <CardBody className="p-4">
                <Form>
                  <h1>S'identifier</h1>
                  <InputGroup className="mb-3">
                    <InputGroupAddon addonType="prepend">
                      <InputGroupText>@</InputGroupText>
                    </InputGroupAddon>
                    <Input type="text" placeholder="Email" autoComplete="email" required/>
                  </InputGroup>
                  <InputGroup className="mb-3">
                    <InputGroupAddon addonType="prepend">
                      <InputGroupText>
                        <i className="icon-lock"></i>
                      </InputGroupText>
                    </InputGroupAddon>
                    <Input type="password" placeholder="Mot de passe" autoComplete="new-password" required/>
                  </InputGroup>
                  <Button color="success" block>Se connecter</Button>
                </Form>
              </CardBody>
              </Card>
          </Col>
        </Row>
      </Container>
    </div>
        );
    }
}

export default Login;
