import axios from 'axios'
import { BASE_PATH, SIGNALS, SIGNAL, SIGNAL_AUTHOR } from '../paths/index'

export function loadSignals(page){
  return axios
     .get(BASE_PATH + SIGNALS, {
     headers: { Authorization: `Bearer ${axios.defaults.headers.common.Bearer}` }
    })
     .then(response => {
       console.log(response);
       return response
        })
      .catch(err => {
           console.log(err)
      })
}

export function loadSignal(signal_id) {
  return axios
      .get(BASE_PATH + SIGNAL(signal_id), {
        headers: { Authorization: `Bearer ${axios.defaults.headers.common.Bearer}` }
      })
      .then(response => {
        console.log(response)
        return response
      })
      .catch(err => {
        console.log('err', err);
      })
  }

export function loadSignal_author(signal_author) {
  return axios
  .get(BASE_PATH + SIGNAL_AUTHOR(signal_author), {
    headers: { Authorization: `Bearer ${axios.defaults.headers.common.Bearer}` }
  })
   .then(res => {
      console.log(res);
      return res
    })
    .catch(err => {
      console.log('err', err);
    })
}


