import React, { Component } from 'react';
import {Form,Button,Badge, Card, CardBody, CardHeader, Col, Row, Table} from 'reactstrap';
import wretch from 'wretch';
import Moment from 'moment';
import mapboxgl from 'mapbox-gl'
import { relative } from 'path';
import { MAP_TOKEN, MAP_IMAGE } from '../../api/paths/index'
import { loadSignalsToMap } from '../../api/functions/SignalsFunctions'


class SignalsMap extends Component {
  _isMounted = false;

  constructor(props) {
    super(props);
    this.state = {
      items: {},
      isLoading: false,
      categoryValue: '',
      statusValue: ''
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({ [event.target.name]: event.target.value })
  }

  handleSubmit(event) {
    event.preventDefault()
    console.log(this.state);
  }

  async componentDidMount() {
    this._isMounted = true;
  //  const res = await this.loadSignal();
    let res

    loadSignalsToMap().then(response => {
      if (this._isMounted)
        this.setState({
          isLoading: true
        });
        if (response) {
          res = response.data
          this.setState({
           isLoading: false,
           items: response,
         });
          }
      })

      console.log(this.state);
     
    mapboxgl.accessToken = MAP_TOKEN;
    var map = new mapboxgl.Map({
      container: this.Mapcontainer,
      style: 'mapbox://styles/mapbox/streets-v9',
      center: [2.4159, 48.7651],
      zoom: 13
    });

    map.on("load", function () {
          map.loadImage(MAP_IMAGE, async function(error, image) {
          if (error) throw error;
          await res.forEach(
            signal => {
            map.addImage(signal._id, image);
            map.addLayer({
              id: signal._id,
              type: "symbol",
              source: {
                type: "geojson",
                data: {
                  type: 'FeatureCollection',
                  features: [
                    {
                      type: 'Feature',
                      properties: {
                        "description":"<strong>"+ signal.name +"</strong><p>"+
                        signal.description + "</p> <p>Date: "+ Moment(signal.created_date).format('DD/MM/YY à HH:mm') +
                        "</p> <p><a href='"+ `#/signal/${signal._id}/` +"'><em>Voir la fiche du signalement</em></a> </p>"
                      },
                      geometry: {
                      type: "Point",
                      coordinates: [signal.localization.long, signal.localization.lat]
                      }
                    }
                  ]
                }
              },
              layout: {
                "icon-image": signal._id,
                "icon-allow-overlap": true
              }
            });
            map.on('click', signal._id, function (e) {
              var coordinates = e.features[0].geometry.coordinates.slice();
              var description = e.features[0].properties.description;
              while (Math.abs(e.lngLat.lng - coordinates[0]) > 180) {
              coordinates[0] += e.lngLat.lng > coordinates[0] ? 360 : -360;
              }
              new mapboxgl.Popup()
              .setLngLat(coordinates)
              .setHTML(description)
              .addTo(map);
              });
            }
          );
        })
      });
  }

  componentWillUnmount() {
    this._isMounted = false;
  }

  async componentDidUpdate(prevProps, prevState, snapshot) {
    if (prevProps.match.params.id !== this.props.match.params.id)
      await this.loadSignal()
  }

  render() {
    return (
      <div className="animated fadeIn">
        <Row>
          <Col lg={12}>
            <Card>
              <CardHeader className="card card-accent-warning mb-3" >
                <strong><i className="icon-info pr-1"></i>Localisation</strong>
              <div class="dropdown" style={{display: 'inline-block'}}>
                <Form noValidate onSubmit={this.handleSubmit}>
                <select name="statusValue" value={this.state.statusValue} onChange={this.handleChange} className="form-select form-select-lg mb-3" style={{marginLeft: '20%', fontWeight: 'bold', fontFamily: 'cursive', border: '3px solid transparent'}}>
                <option value="">Afficher par status de signalement</option>
                <option value="Ouvert">Ouvert</option>
                <option value="Terminé">Terminé</option>
                <option value="En Attente">En Attente</option>
                <option value="En Cours">En Cours</option>
                </select>
                <select name="categoryValue" value={this.state.categoryValue} onChange={this.handleChange} className="form-select form-select-sm" style={{marginLeft: '15%', fontWeight: 'bold', fontFamily: 'cursive', border: '3px solid transparent'}}>
                <option value="">Afficher par categorie de signalement</option>
                <option value="Propreté">Propreté</option>
                <option value="Panne">Panne</option>
                <option value="Dégradation">Dégradation</option>
                </select>
                <Button color="success" type="submit" block style={{width: '20%', marginLeft: "43%"}}>Lancer recherche</Button>
                </Form>
              </div>
              </CardHeader>
              <CardBody>
              <div className='FullMap' ref={(x) => { this.Mapcontainer = x }}></div>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
    )
  }
}

export default SignalsMap;
