import React, { Component } from 'react';
import {Badge, Card, CardBody, CardHeader, Col, Row, Table} from 'reactstrap';
import wretch from 'wretch';
import Moment from 'moment';
import mapboxgl from 'mapbox-gl'
import { relative } from 'path';
import { loadSignalss, loadSignalsss } from '../../api/functions/SignalsFunctions'



class Signal extends Component {
  _isMounted = false;

  constructor(props) {
    super(props);
    this.state = {
      item: {},
      user: {},
      isLoading: false
    };
  }

  async componentDidMount() {
    this._isMounted = true;
  //  await this.loadSignal();

    loadSignalss(this.props.match.params.id).then(response => {
      if (this._isMounted)
        this.setState({
          isLoading: true
        });
    if (response) {
      loadSignalsss(response.data.author).then(resUser => {
        console.log("RESPONSEEEEEEEEEEEEEE");
        console.log(response);
        console.log(resUser);
        if (this._isMounted)
        this.setState({
          user: resUser.data,
          isLoading: false,
          item: response.data,
          });
        });
        }
    })

    mapboxgl.accessToken = 'pk.eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI1ZGI4MTIzOWRmN2U2ZDRiNjgwZjQyZTEiLCJpYXQiOjE2MDA3Nzk3ODZ9.pmNOT1kl1a-ylGCOudg8JVabUOAuW3gRjuh2zSCmruQ';

    var map = new mapboxgl.Map({
      container: this.Mapcontainer,
      style: 'mapbox://styles/mapbox/streets-v9',
      center: [2.4159, 48.7651],
      zoom: 16
    });

    map.on("load", function () {
      map.loadImage("https://i.imgur.com/MK4NUzI.png", function(error, image) {
        if (error) throw error;
        map.addImage("custom-marker", image);
        map.addLayer({
          id: "markers",
          type: "symbol",
          source: {
            type: "geojson",
            data: {
              type: 'FeatureCollection',
              features: [
                {
                  type: 'Feature',
                  properties: {},
                  geometry: {
                  type: "Point",
                  coordinates: [2.4162, 48.7649]
                  }
                }
              ]
            }
          },
          layout: {
            "icon-image": "custom-marker",
          }
        });
      });
    });
  }

  componentWillUnmount() {
    this._isMounted = false;
  }

  async componentDidUpdate(prevProps, prevState, snapshot) {
    if (prevProps.match.params.id !== this.props.match.params.id)
      await this.loadSignal()
  }


  render() {
    const {item: signal, isLoading, user: user} = this.state
    console.log(this.state);
    if (isLoading) return null

    console.log('signal', signal)
    console.log('user ', user);

    return (
      <div className="animated fadeIn">
        <Row>
          <div className="divSameHeight">
            <Col lg={6} className="sameHeight">
              <Card  className="fullHeight">
                <CardHeader className="card card-accent-primary mb-3">
                  <strong><i className="icon-info pr-1"></i>Signalement</strong>
                </CardHeader>
                <CardBody>
                  <Table responsive striped hover>
                    <tbody>
                    {/*<tr>*/}
                      <tr>
                        <th>Auteur du signalement</th>
                        <td>{signal._id}</td>
                      </tr>
                      <tr>
                        <th>Nom</th>
                        <td>{signal.name }</td>
                      </tr>
                      <tr>
                        <th>Catégorie</th>
                        <td>{signal.category}</td>
                      </tr>
                      <tr>
                        <th>Description</th>
                        <td>{signal.description}</td>
                      </tr>
                      <tr>
                        <th>Ville</th>
                        <td>{signal.city}</td>
                      </tr>
                      <tr>
                        <th>Status</th>
                        <td>{signal.status}</td>
                      </tr>
                      <tr>
                        <th>Date</th>
                        <td>{Moment(signal.date).format('DD/MM/YY à HH:mm')}</td>
                      </tr>
                      <tr>
                        <th>Date de création</th>
                        <td>{Moment(signal.created_date).format('DD/MM/YY à HH:mm')}</td>
                      </tr>
                      <tr>
                        <th>Dernière mis à jour</th>
                        <td>{Moment(signal.last_update).format('DD/MM/YY à HH:mm')}</td>
                      </tr>
                    </tbody>
                  </Table>
                </CardBody>
              </Card>
            </Col>

            <Col lg={6}  className="sameHeight">
              <Card className="fullHeight">
                <CardHeader className="card card-accent-warning mb-3" >
                  <strong><i className="icon-info pr-1"></i>Localisation</strong>
                </CardHeader>
                <CardBody >
                <div className='MapContent' ref={(x) => { this.Mapcontainer = x }}></div>
                </CardBody>
              </Card>
            </Col>
          </div>

          <Col lg={12}>
            <Card>
              <CardHeader className="card card-accent-success mb-3">
                <strong><i className="icon-info pr-1"></i>Auteur du signalement</strong>
              </CardHeader>
              <CardBody>
                <Table responsive striped hover>
                  <thead>
                  <tr>
                    <th scope="col">Civilité</th>
                    <th scope="col">Nom</th>
                    <th scope="col">Email</th>
                    <th scope="col">Numéro de téléphone</th>
                  </tr>
                  </thead>
                  <tbody>
                    <tr key={user._id}>
                      <td>{user.gender}</td>
                      <td><strong>{`${user.firstName} ${user.lastName}`}</strong></td>
                      <td>{user.email}</td>
                      <td>{user.phone}</td>
                    </tr>
                  </tbody>
                </Table>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </div>
    )
  }
}

export default Signal;
